import e from 'express';
import React, { useState } from 'react';
import UserService from '../service'

function Form() {
  const [createUser,setCreateUser] =useState({
    id:"",
    name:"",
    email:"",
  });
    
  const handleChange=(e) =>{
    const value =e.target.value;
    setCreateUser({...createUser,[e.target.name]:value});
  }

  const saveUser =(e)=>{
    e.preventDefault();
    UserService.saveUser(createUser);

  }
  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <form className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
        <h2 className="text-2xl font-bold mb-6 text-red-600 text-center ">User Information</h2>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm  font-bold mb-2" htmlFor="id">
            ID
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            name='id'
            type="number"
            noChange={(e)=> handleChange(e)}
            placeholder="Enter your ID"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="name">
            Name
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            
            name='name'
            type="text"
            noChange={(e)=> handleChange(e)}
            placeholder="Enter your name"
          />
        </div>
        <div className="mb-6">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="email">
            Email
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
           
            name='email'
            type="email"
            noChange={(e)=> handleChange(e)}
            placeholder="Enter your email"
          />
        </div>
        <div className="flex items-center justify-between">
          <button
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            type="button"
            onClick={saveUser}
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
}

export default Form;
